import java.util.Scanner;public class Home_Work_1 {    /* Создать программу, выводящую на экран ближайшее к 10 из двух чисел, записанных в переменные m и n.       Числа могут быть, как целочисленные, так и дробные.       Например :       ввод : m=10.5, n=10.45       вывод: Число 10.45 ближе к 10. */    public static double finPoint = 10.0;    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.print("Введите первое число для сравнения: ");        double num1 = sc.nextDouble();        System.out.print("Введите второе число для сравнения: ");        double num2 = sc.nextDouble();        printNum(num1, num2);    }    public static void printNum(double num1, double num2) {        double res1 = numberDifference(num1);        double res2 = numberDifference(num2);        if (res1 < res2) {            System.out.println("Число " + num1 + " ближе к 10");        } else if (res2 < res1) {            System.out.println("Число " + num2 + " ближе к 10");        } else {            System.out.println("Числа находятся на одинаковом растоянии к 10");        }    }    public static double numberDifference(double num) {        double res = num - finPoint;        if (res >= 0) {            return res;        } else return -res;    }}