import java.util.Scanner;public class Home_Work_3 {    /* Дополнительная практика(не обязательное)       Создайте метод, который будет считать сколько денег получает работник в неделю. Метод должен принимать на входе       два аргумента (зарплата в час, кол-во проработанных часов).       Каждый час после 40 считается за полтора.       Работник не может работать больше, чем 60 часов в неделю.       Работник не может получать меньше 8 долларов в час. */    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.print("Введите зарплату зв один час: ");        double salaryHours = sc.nextDouble();        System.out.print("Введите количество отработанных часов: ");        double workedHours = sc.nextDouble();        calculatesSalary(salaryHours, workedHours);    }    public static void calculatesSalary(double salaryHour, double workedHours) {        double maxHours = 60.0;        double normalHours = 40.0;        if (salaryHour < 8) {            salaryHour = 8;        }        if (workedHours > maxHours) {            System.out.println("Вы умерли от работы и Вам не нужна зарплата!");        } else if (workedHours <= normalHours) {            double salary = workedHours * salaryHour;            System.out.println("Вы заработали на этой неделе: " + salary);        } else {            double overtimeHours = (workedHours - normalHours) * 1.5;            double salary = (normalHours * salaryHour) + (overtimeHours * salaryHour);            System.out.println("Вы заработали на этой неделе: " + salary);        }    }}